//refAssemblies: TCAdmin.SDK.dll, TCAdmin.GameHosting.SDK.dll, TCAdmin.Scripting.dll, TCAdmin.Monitor.dll
using System;
using System.Diagnostics;
using System.IO;
using System.IO.Compression;
using System.Net;
using System.Net.Http;
using TCAdmin.SDK.Helpers;
using TCAdmin.SDK.Misc.Extensions;

var Globals = new TCAdmin.Scripting.Engines.Addons.CSharpGameGlobals(); // DO NOT MODIFY THIS LINE

using(var p = new Process())
{
    p.StartInfo.FileName = "git.exe";
    try
    {
        p.Start();
    }
    catch (Exception)
    {
        throw new Exception("Please install git with \"Use Git from the Windows Command Prompt\" checked and restart the monitor.");
    }
};

void WriteLog(string s, int? progress = null, bool rewriteLastLog = false)
{
    if(Globals.ThisTaskStepHandler != null)
    {
        Globals.ThisTaskStepHandler.UpdateProgress(progress: progress, log: s);
    }
    else if(Globals.ThisTaskStep!=null)
    {
        if(progress.HasValue)
        {
            Globals.ThisTaskStep.Progress = progress.Value;
        }

        if(rewriteLastLog)
        {
            Globals.ThisTaskStep.DisplayLog = s;
            Globals.ThisTaskStep.DisplayDebugLog = s;
        }
        else
        {
            Globals.ThisTaskStep.AddDebugLog(s, skipDuplicates:true);
        }
        Globals.TCATaskManager.Update(Globals.ThisTask, true);
    }
    else
    {
        Globals.ScriptConsole.WriteLine(s);
    }
}

var gameFilesPath = Globals.ThisServer.Configuration.GameFilesPath;
var fiveMPath = Path.Combine(gameFilesPath, Globals.ThisGame.FileAndDirectoryConfig.AutoSetupFolderName);
var downloadedFile = Path.Combine(gameFilesPath, "__fivem.7z");
var tools = Path.Combine(InstallLocator.GetInstallRoot(), "data", "tools");
if(!Directory.Exists(tools)) Directory.CreateDirectory(tools);
var _7zaUrl = "https://www.7-zip.org/a/7za920.zip";
var _7zaZip = Path.Combine(InstallLocator.GetInstallRoot(), "data", "tools", "7za.zip");
var _7zaPath = Path.Combine(InstallLocator.GetInstallRoot(), "data", "tools", "7za");
var _7zaExe = Path.Combine(_7zaPath, "7za.exe");

using (HttpClient downloadClient = new HttpClient())
{
    // Download fivem server files
    await downloadClient.DownloadFile("http://downloads.tcadmin.net/games/fivem-windows-7z.aspx", downloadedFile, Globals.ThisCancellationToken);
}

// Download 7zip if needed
if (!File.Exists(_7zaExe))
{
    WriteLog("Downloading 7zip...");
    using var wc = new HttpClient();
    using var response = await wc.GetAsync(_7zaUrl, Globals.ThisCancellationToken);
    using var fs = File.Create(_7zaZip);
    await response.Content.CopyToAsync(fs, Globals.ThisCancellationToken);
    fs.Close();

    ZipFile.ExtractToDirectory(_7zaZip, _7zaPath);
    File.Delete(_7zaZip);
}

// Extract fivem.7z
using (var p = new Process())
{
    p.StartInfo.FileName = _7zaExe;
    p.StartInfo.Arguments = $"x -y -o\"{fiveMPath}\" \"{downloadedFile}\"";
    p.StartInfo.WorkingDirectory = _7zaPath;
    p.Start();
    p.WaitForExit();
}

File.Delete(downloadedFile);

// Download server-data
using (var p = new Process())
{
    p.StartInfo.FileName = "git.exe";
    p.StartInfo.Arguments = "clone https://github.com/citizenfx/cfx-server-data.git server-data";
    p.StartInfo.WorkingDirectory = fiveMPath;
    p.Start();
    p.WaitForExit();
}
