default = "Java 21"
# Set Automatic Java version if the variable doesn't exist.
if not ThisGameService.Variables.ContainsKey("JavaVersion"):
  ThisGameService.Variables["JavaVersion"] = 'Automatic'

# Used for mapping Java versions to environment variables
entrypointMappings = {
    "Java 8": "java8",
    "Java 11": "java11",
    "Java 16": "java16",
    "Java 17": "java17",
    "Java 21": "java21"
}  
  
# Function to compute SHA-256 checksum of a file
def compute_sha256(file_path):
  sha256 = hashlib.sha256()
  with open(file_path, 'rb') as f:
    for chunk in iter(lambda: f.read(4096), b""):
      sha256.update(chunk)
  return sha256.hexdigest()

# Function to read the manifest file and return the version
def read_manifest(jar_file):
  try:
    with zipfile.ZipFile(jar_file, 'r') as jar:
      if 'META-INF/MANIFEST.MF' in jar.namelist():
        manifest = jar.read('META-INF/MANIFEST.MF').decode('utf-8').splitlines()
        for line in manifest:
          if line.startswith('Build-Jdk'):
            return parse_build_jdk(line)
          elif line.startswith('Main-Class'):
            main_class = line.split(': ')[1].strip().replace('.', '/')
            return check_main_class(jar, main_class)
  except Exception as e:
    print("Error reading MANIFEST.MF: {0}".format(e))
  return None

# Helper to parse Build-Jdk entry in META-INF/MANIFEST.MF
def parse_build_jdk(line):
  build_jdk = line.split(': ')[1].strip()
  major_version = '.'.join(build_jdk.split('.')[:2])
  return "Java " + str(major_version)

# Function to check Main-Class if found in MANIFEST.MF
def check_main_class(jar, main_class_path):
  main_class_file = main_class_path + ".class"
  if main_class_file in jar.namelist():
    return extract_java_version_from_class(jar.read(main_class_file))
  return None

# Function to extract Java version from a .class file
def extract_java_version_from_class(class_data):
  if len(class_data) >= 8:
    major_version = int.from_bytes(class_data[6:8], byteorder='big')
    return {
      45: 'Java 1.1',
      46: 'Java 1.2',
      47: 'Java 1.3',
      48: 'Java 1.4',
      49: 'Java 5',
      50: 'Java 6',
      51: 'Java 7',
      52: 'Java 8',
      53: 'Java 9',
      54: 'Java 10',
      55: 'Java 11',
      56: 'Java 12',
      57: 'Java 13',
      58: 'Java 14',
      59: 'Java 15',
      60: 'Java 16',
      61: 'Java 17',
      62: 'Java 18',
      63: 'Java 19',
      64: 'Java 20',
      65: 'Java 21'
    }.get(major_version, 'Unknown Java version')
  return 'Unknown Java version'

# Function to check version.json in a nested jar
def check_version_json(jar_file):
  try:
    with zipfile.ZipFile(jar_file, 'r') as jar:
      return check_root_version_json(jar) or check_nested_version_json(jar)
  except Exception as e:
    print("Error reading version.json: {0}".format(e))
  return None

# Helper to check the root version.json
def check_root_version_json(jar):
  if 'version.json' in jar.namelist():
    with jar.open('version.json') as version_file:
      return extract_java_version_from_json(version_file)

# Helper to check nested jars for version.json
def check_nested_version_json(jar):
  for name in jar.namelist():
    if name.startswith('META-INF/versions/') and name.endswith('.jar'):
      with jar.open(name) as nested_jar_file:
        with zipfile.ZipFile(BytesIO(nested_jar_file.read())) as nested_jar:
          if 'version.json' in nested_jar.namelist():
            with nested_jar.open('version.json') as version_file:
              return extract_java_version_from_json(version_file)

# Function to extract Java version from version.json
def extract_java_version_from_json(version_file):
  version_data = json.loads(version_file.read().decode('utf-8'))
  java_version = version_data.get('java_version')
  if java_version:
    print("Found version in version.json: Java {0}".format(java_version))                            
    return "Java " + str(java_version)
  return None

# Function to process a single jar file
def process_jar(jar_file):
  jar_sha256 = compute_sha256(jar_file)
  sha256_folder = os.path.join(ThisService.RootDirectory, 'Temp', 'MinecraftSHA256')
  sha256_file = os.path.join(sha256_folder, jar_sha256)

  # Check if SHA-256 checksum exists and read the Java version from it
  
  if not os.path.isdir(sha256_folder):
    os.mkdir(sha256_folder)
  if os.path.exists(sha256_file):
    with open(sha256_file, 'r') as f:
      saved_java_version = f.read().strip()
      print("SHA-256 checksum matched ({0}), using cached Java version: {1}".format(sha256_file, saved_java_version))
      return saved_java_version

  # Process jar and determine highest Java version
  print("Processing: {0}".format(jar_file))

  java_versions = [
    read_manifest(jar_file),
    check_version_json(jar_file)
  ]

  with zipfile.ZipFile(jar_file, 'r') as jar:
    class_files = [name for name in jar.namelist() if name.endswith('.class')]
    java_versions.extend([extract_java_version_from_class(jar.read(class_file)) for class_file in class_files])

  highest_version = determine_highest_java_version(java_versions)
  print("Highest Java version: {0}".format(highest_version))
  # Save the SHA-256 and highest Java version in a file
  with open(sha256_file, 'w') as f:
    f.write("{0}".format(highest_version))

  return highest_version

# Determine the highest Java version with version grouping logic
def determine_highest_java_version(java_versions):
  adjusted_versions = []
  for version in java_versions:
    if version and version.startswith("Java ") and version != "Unknown Java version":
      version_number = version.split(" ")[1]
      if float(version_number) <= 8:
        adjusted_versions.append("Java 8")
      elif float(version_number) <= 11:
        adjusted_versions.append("Java 11")
      elif float(version_number) <= 16:
        adjusted_versions.append("Java 16")
      elif float(version_number) == 17:
        adjusted_versions.append("Java 17")
      else:
        adjusted_versions.append("Java 21")

  return max(adjusted_versions, key=lambda v: list(map(int, v.split(" ")[1].split('.'))), default='Java 21')

# Parse major version from version string
def parse_major_version(version_number):
  try:
    return int(version_number.split(".")[0]) if len(version_number.split(".")) == 1 else int(version_number.split(".")[1])
  except ValueError:
    return 0  # Fallback to 0 for invalid versions

# Function to extract the version number from a filename
def extract_version_from_filename(filename):
  version_match = re.search(r"(\d+\.\d+(\.\d+)?)", filename)
  return version_match.group(0) if version_match else None

def search_jars(directory='.', extension=''):
  extension = extension.lower()
  for dirpath, dirnames, files in os.walk(directory):
    for name in files:
      if extension and name.lower().endswith(extension):
        return os.path.join(dirpath, name)
      elif not extension:
        return os.path.join(dirpath, name) 
        
startup = ThisService.Commandline
def getJarFromStartup():
  splitted_commandline = startup.split()
  if not '-jar' in startup:
    # Find the path segment starting with "@" and ending with ".txt" - takes care of Minecraft Forge and NeoForge
    print('Jar not found in commandline')
