// CurseForge class
class CurseForgeModpacks : IDisposable
{
    private readonly string _modpackId;
    private readonly string _modpackVersion;
    private readonly string _modLoaderType;
    private readonly string _gameVersion;
    private readonly bool _deleteWorldFolders;
    private readonly string _apiKey;
    private readonly int _totalSteps;
    private readonly HttpClient _httpClient;
    private readonly string _tcAdminFolder;
    private readonly MinecraftModpacksProvider _provider;
    private readonly CSharpGlobals Globals;

    public CurseForgeModpacks(CSharpGlobals globals, int totalSteps)
    {
        Globals = globals;
        _tcAdminFolder = Globals.TCAdminConfig.TCAdminPath;
        _modpackId = Globals.GetValue<string>("ModId");
        _modpackVersion = Globals.GetValue<string>("ModVersion");
        _modLoaderType = Globals.GetValue<string>("LoaderType");
        _gameVersion = Globals.GetValue<string>("MinecraftVersion");
        _deleteWorldFolders = Globals.GetValue<bool>("DeleteWorld");
        _provider = Globals.GetValue<MinecraftModpacksProvider>("ModProvider");
        _apiKey = TCAdmin.SDK.GameHosting.Curse.CurseBrowser.CURSE_API_KEY;
        _totalSteps = _deleteWorldFolders ? totalSteps - 2 : totalSteps - 1;
        _httpClient = new HttpClient();
        _httpClient.DefaultRequestHeaders.Add("x-api-key", _apiKey);
    }

    public void WriteLog(string s, int? progress = null, bool rewriteLastLog = false)
    {
        if(Globals.ThisTaskStep!=null)
        {
            if(progress.HasValue)
            {
                Globals.ThisTaskStep.Progress = progress.Value;
            }

            if(rewriteLastLog)
            {
                Globals.ThisTaskStep.DisplayLog = s;
                Globals.ThisTaskStep.DisplayDebugLog = s;
            }
            else
            {
                Globals.ThisTaskStep.AddDebugLog(s, skipDuplicates:true);
            }
            Globals.TCATaskManager.Update(Globals.ThisTask, true);
        }
        else
        {
            Globals.ScriptConsole.WriteLine(s);
        }
    }

    public void SetDisplayMessage(string s)
    {
        if(Globals.ThisTaskStep!=null)
        {
            Globals.ThisTaskStep.DisplayLog = s;
            Globals.ThisTaskStep.DisplayDebugLog = s;
            Globals.TCATaskManager.Update(Globals.ThisTask, true);
        }
        else
        {
            Globals.ScriptConsole.WriteLine(s);
        }
    }

    public void UpdateProgress(int progress)
    {
        if(Globals.ThisTaskStep != null && progress!=Globals.ThisTaskStep.Progress)
        {
            Globals.ThisTaskStep.Progress=progress;
            Globals.TCATaskManager.Update(Globals.ThisTask, true);
        }
    }

    public async Task<LatestFile> GetModpackInformation()
    {
        WriteLog($"1/{_totalSteps} - Getting information about the modpack ID {_modpackId} version {_modpackVersion}...");
        
        var modpackInfo = await CurseBrowser.GetFile(_modpackId, _modpackVersion);
        
        if (!modpackInfo.IsServerPack && modpackInfo.ServerPackFileId > 0)
        {
            var serverPackId = modpackInfo.ServerPackFileId.ToString();
            WriteLog($"1/{_totalSteps} - Serverpack ID: {serverPackId}");
            var serverpackInfo = await CurseBrowser.GetFile(_modpackId, serverPackId);
            
            if (serverpackInfo.GameVersions.Count == 0)
            {
                //modpackInfo = serverpackInfo.Clone();
                serverpackInfo.GameVersions = modpackInfo.GameVersions;
                serverpackInfo.SortableGameVersions = modpackInfo.SortableGameVersions;
                modpackInfo = serverpackInfo;
            }
            else
            {
                modpackInfo = serverpackInfo;
            }
            WriteLog($"1/{_totalSteps} - Serverpack information received");
        }
        
        UpdateProgress(33);
        return modpackInfo;
    }

    public async Task<string> DownloadModpack(LatestFile modpackInfo)
    {
        var fileName = modpackInfo.FileName;
        var fileSize = modpackInfo.FileLength / (1024 * 1024);
        var downloadUrl = modpackInfo.DownloadUrl;
        
        if (string.IsNullOrEmpty(downloadUrl))
        {
            var fileId = modpackInfo.Id;
            downloadUrl = $"https://edge.forgecdn.net/files/{fileId.Substring(0, 4)}/{fileId.Substring(fileId.Length - 3)}/{fileName}";
        }

        WriteLog($"2/{_totalSteps} - Downloading modpack \"{fileName}\" ({fileSize}MB)");
        var filePath = Path.Combine(Globals.ThisService.RootDirectory, fileName);
        
        
        using (var response = await _httpClient.GetAsync(downloadUrl, HttpCompletionOption.ResponseHeadersRead, Globals.ThisCancellationToken))
        using (var stream = await response.Content.ReadAsStreamAsync())
        using (var fileStream = new FileStream(filePath, FileMode.Create))
        {
            var totalBytes = response.Content.Headers.ContentLength ?? 0;
            var buffer = new byte[8192];
            var bytesRead = 0;
            var totalRead = 0L;
            
            while ((bytesRead = await stream.ReadAsync(buffer, 0, buffer.Length)) > 0)
            {
                Globals.ThisCancellationToken.ThrowIfCancellationRequested();
                await fileStream.WriteAsync(buffer, 0, bytesRead);
                totalRead += bytesRead;
                var progress = (int)((double)totalRead / totalBytes * 100);
                if(progress % 5 == 0)
                {
                    UpdateProgress(progress);
                    //WriteLog($"2/{_totalSteps} - Downloading modpack \"{fileName}\" ({fileSize}MB, {progress}%)", progress, true);
                }
            }
        }
        
        UpdateProgress(100);
        return fileName;
    }

    public async Task<(string installerJar, string specificModloaderVersion)> ExtractModpack(string fileName)
    {
        UpdateProgress(0);
        var specificModloaderVersion = (string)null;
        WriteLog($"3/{_totalSteps} - Extracting \"{fileName}\"");
        
        var zipFile = Path.Combine(Globals.ThisService.RootDirectory, fileName);
        var tmpDir = Path.Combine(Globals.ThisService.RootDirectory, $"{_modpackId}-tmp");
        if(!Directory.Exists(tmpDir)) Directory.CreateDirectory(tmpDir);

        var c = new CompressionTools(Globals.OperatingSystem)
        {
            ThrowException = true,
            KeepListOfExtractedFiles = true,
            SetFileOwnerAutomatically = true
        };
        c.Extracting+=(o, e) => {
            var progress = (int)e.Progress;
            if(progress % 5 == 0)
            {
                UpdateProgress(progress);
            }
        };
        c.Decompress(zipFile, tmpDir, Globals.ThisCancellationToken);
        
        WriteLog($"3/{_totalSteps} - Extraction complete");
        var extractedFiles = c.ExtractedFiles;
        var regex = Globals.ThisServer.OperatingSystem == EOperatingSystem.Windows
            ? new Regex(@"^.*[\\/]+mods[\\/]+.*\.jar$")
            : new Regex(@"^.*/mods/.*\.jar$");

        var modsFound = extractedFiles.Any(f => regex.IsMatch(f));
        string installerJar = null;

        if (!modsFound && File.Exists(Path.Combine(tmpDir, "mods.csv")))
        {
            WriteLog($"3/{_totalSteps} - No mods folder, but mods.csv available. Downloading files...");
            var downloadFiles = File.ReadAllLines(Path.Combine(tmpDir, "mods.csv"))
                                .Select(line => line.Split(','))
                                .Where(parts => parts.Length >= 2)
                                .ToList();
            
            var totalFiles = downloadFiles.Count;
            for (var i = 0; i < totalFiles; i++)
            {
                Globals.ThisCancellationToken.ThrowIfCancellationRequested();
