    public async Task<(string[] modloaderInfo, bool isNeo)> InstallModloader(LatestFile modloader, string includedInstallerJar, string specificModloaderVersion)
    {
        WriteLog($"4/{_totalSteps} - Beginning modloader installation");
        UpdateProgress(0);
        
        var modloaderType = modloader.SortableGameVersions
            .FirstOrDefault(v => v.GameVersionTypeId == 68441)?.GameVersionName;

        var gameVersions = modloader.GameVersions.ToList();

        if (gameVersions.Count < 2 && string.IsNullOrEmpty(_modLoaderType))
            throw new Exception("You need to specify a modloader to install");

        gameVersions.Sort();
        var gameVersion = !string.IsNullOrEmpty(_gameVersion) ? _gameVersion : gameVersions[0];
        var isNeo = false;
        string modloaderVersion = null;
        string installerJar = null;
        string modloaderJar = null;

        if (string.IsNullOrEmpty(includedInstallerJar) || specificModloaderVersion != null)
        {
            if (_modLoaderType == "Forge" || _modLoaderType == "NeoForge" || 
                modloaderType == "Forge" || modloaderType == "NeoForge")
            {
                isNeo = _modLoaderType == "NeoForge" || modloaderType == "NeoForge";
                var loaderResponse = _httpClient.GetAsync($"https://api.curseforge.com/v1/minecraft/modloader?version={gameVersion}", Globals.ThisCancellationToken).Result;
                loaderResponse.EnsureSuccessStatusCode();
                var loaderData = JsonDocument.Parse(loaderResponse.Content.ReadAsStringAsync().Result);
                var latestVersions = loaderData.RootElement.GetProperty("data").EnumerateArray()
                    .Where(v => v.GetProperty("latest").GetBoolean())
                    .Select(v => v.GetProperty("name").GetString())
                    .ToList();
                
                modloaderVersion = latestVersions.Max();
                var versionResponse = _httpClient.GetAsync($"https://api.curseforge.com/v1/minecraft/modloader/{modloaderVersion}", Globals.ThisCancellationToken).Result;
                versionResponse.EnsureSuccessStatusCode();
                var versionData = JsonDocument.Parse(versionResponse.Content.ReadAsStringAsync().Result);
                var forgeVersion = versionData.RootElement.GetProperty("data").GetProperty("forgeVersion").GetString();
                modloaderVersion = specificModloaderVersion ?? forgeVersion;
                
                // Handle old Forge versions
                if ((_modLoaderType == "Forge" || modloaderType == "Forge") && 
                    new Version(modloaderVersion).CompareTo(new Version("12.18.0.2008")) < 0)
                {
                    modloaderVersion += "-" + gameVersion;
                }
                
                if (isNeo)
                {
                    var mcVersion = versionData.RootElement.GetProperty("data").GetProperty("minecraftVersion").GetString();
                    if(new Version(mcVersion).CompareTo(new Version("1.20.1")) > 0)
                    {
                        var neoforgePrefix = GetNeoForgeVersion(gameVersion);

                        // Step 1: Download the XML
                        var  MetadataUrl = "https://maven.neoforged.net/releases/net/neoforged/neoforge/maven-metadata.xml";
                        using var client = new HttpClient();
                        var xmlData = await client.GetStringAsync(MetadataUrl);
                        
                        // Step 2: Parse the XML
                        var doc = XDocument.Parse(xmlData);
                        var versions = doc
                        .Root
                        .Element("versioning")
                        .Element("versions")
                        .Elements("version")
                        .Select(x => x.Value);

                        // Step 3: Filter by prefix
                        var matching = versions
                            .Where(v => v.StartsWith(neoforgePrefix, StringComparison.OrdinalIgnoreCase))
                            .ToList();

                        if (matching.Count == 0)
                                    throw new InvalidOperationException(
                                        $"Could not find a Neoforge version starting with '{neoforgePrefix}'.");

                        // Step 4: Sort and pick latest
                        matching.Sort(CompareVersionKeys);
                        modloaderVersion = matching[^1];  // last element

                        installerJar = $"https://maven.neoforged.net/releases/net/neoforged/neoforge/{modloaderVersion}/neoforge-{modloaderVersion}-installer.jar";
                    }
                    else
                    {
                        WriteLog("NeoForge version is smaller than 1.20.1");

                        installerJar = $"https://maven.neoforged.net/releases/net/neoforged/forge/{gameVersion}-{modloaderVersion}/forge-{gameVersion}-{modloaderVersion}-installer.jar";

                    }
                }

                if(!isNeo)
                {
                    installerJar = $"https://maven.minecraftforge.net/net/minecraftforge/forge/{gameVersion}-{modloaderVersion}/forge-{gameVersion}-{modloaderVersion}-installer.jar";
                }
                
                modloaderJar = versionData.RootElement.GetProperty("data").GetProperty("filename").GetString();
            }
            else if (_modLoaderType == "Fabric" || modloaderType == "Fabric")
            {
                var fabricMetadata = _httpClient.GetStringAsync("https://maven.fabricmc.net/net/fabricmc/fabric-installer/maven-metadata.xml").Result;
                var doc = XDocument.Parse(fabricMetadata);
                modloaderVersion = doc.Descendants("release").First().Value;
                installerJar = $"https://maven.fabricmc.net/net/fabricmc/fabric-installer/{modloaderVersion}/fabric-installer-{modloaderVersion}.jar";
                modloaderJar = "fabric-server-launch.jar";
            }
            
            var installerPath = Path.Combine(Globals.ThisService.RootDirectory, Path.GetFileName(installerJar));

            using (var response = await _httpClient.GetAsync(installerJar, HttpCompletionOption.ResponseHeadersRead, Globals.ThisCancellationToken))
            {
                response.EnsureSuccessStatusCode();
                var totalBytes = response.Content.Headers.ContentLength ?? -1L;
                var canReportProgress = totalBytes != -1;

                using (var contentStream = await response.Content.ReadAsStreamAsync())
                using (var fileStream = new FileStream(installerPath, FileMode.Create, FileAccess.Write, FileShare.None, 8192, true))
                {
                    var buffer = new byte[8192];
                    long totalRead = 0;
                    int read;
                    WriteLog($"4/{_totalSteps} - Downloading modloader \"{installerJar}\"");

                    while ((read = await contentStream.ReadAsync(buffer, 0, buffer.Length)) > 0)
                    {
                        Globals.ThisCancellationToken.ThrowIfCancellationRequested();
                        await fileStream.WriteAsync(buffer, 0, read);
                        totalRead += read;

                        if (canReportProgress)
                        {
                            var progress = (int)((totalRead * 100L) / totalBytes);
                            if(progress % 5 == 0) UpdateProgress(progress);
                        }
                    }
                }
            }
            
            installerJar = Path.GetFileName(installerJar);
        }
        else
        {
            isNeo = _modLoaderType == "NeoForge" || modloaderType == "NeoForge";
            installerJar = includedInstallerJar;
            WriteLog($"4/{_totalSteps} - Modloader installer found in modpack ({installerJar})");
            modloaderJar = installerJar.Replace("-installer", "");
            
            if (modloaderJar.Contains("fabric"))
            {
                modloaderVersion = modloaderJar.Replace("fabric-", "").Replace(".jar", "").Replace("-", "");
                modloaderJar = "fabric-server-launch.jar";
            }
            else
            {
                modloaderVersion = modloaderJar
                    .Replace($"forge-{gameVersion}-", "")
                    .Replace("neoforge-", "")
                    .Replace(".jar", "");
            }
        }

        WriteLog($"4/{_totalSteps} - Installing modloader");
        var args = (_modLoaderType.EndsWith("Forge") || _modLoaderType.EndsWith("Forge") || isNeo)
            ? $"-jar {installerJar} --installServer"
            : $"-jar {installerJar} server -downloadMinecraft -mcversion {gameVersion} -dir {Globals.ThisService.RootDirectory}";
        
        var startInfo = new ServiceToolStartOptions
        {
            Executable = Path.Combine(Globals.ThisService.RootDirectory, Globals.ThisService.Executable),
            WorkingDirectory = Globals.ThisService.RootDirectory,
            Arguments = args
        };

        var startResult = await Globals.ServiceManagerService.StartService(Globals.ThisService.ServiceId, startInfo);

        if(!startResult.Success) throw new Exception("Could not start installer process");

        var p = Process.GetProcessById(startResult.ProcessId);
        p.EnableRaisingEvents=true;
        UpdateProgress(-1);
        Globals.Globals.ThisCancellationToken.Register(() => {
            if(p != null && !p.HasExited) p.Kill(true);
        });
        await p.WaitForExitAsync(Globals.Globals.ThisCancellationToken);
        UpdateProgress(100);
        
        if (p.ExitCode != 0)
        {
            if ((_modLoaderType == "Fabric" || modloaderType == "Fabric") && !string.IsNullOrEmpty(includedInstallerJar))
                throw new Exception("Fabric modloader installation failed. Try another version/modloader");
            
            if ((_modLoaderType.EndsWith("Forge") || modloaderType.EndsWith("Forge")) && !string.IsNullOrEmpty(includedInstallerJar))
            {
                WriteLog($"4/{_totalSteps} - Included Forge failed. Trying latest for {gameVersion}");
                return await InstallModloader(modloader, null, null);
            }
            p=null;
            throw new Exception("Modloader installation failed. Check logs for details");
        }
        else
        {
            p=null;
        }
        
        // Check for universal jar
        if ((_modLoaderType == "Forge" || modloaderType == "Forge") && !isNeo)
        {
            var universalJar = Path.Combine(Globals.ThisService.RootDirectory, installerJar.Replace("-installer", "-universal"));
            WriteLog($"4/{_totalSteps} - Checking if Forge installer created universal jar {universalJar}");
            if (File.Exists(universalJar))
            {
                modloaderJar = Path.GetFileName(universalJar);
                WriteLog($"4/{_totalSteps} - Using universal jar: {modloaderJar}");
            }
        }
        
        UpdateProgress(90);
        WriteLog($"4/{_totalSteps} - Modloader installed");
        
        // Cleanup
        var installerPathToDelete = Path.Combine(Globals.ThisService.RootDirectory, installerJar);
        if (File.Exists(installerPathToDelete)) File.Delete(installerPathToDelete);
        
        UpdateProgress(100);

        return (new[] { modloaderJar, gameVersion, modloaderVersion }, isNeo);
    }

