    public static (List<int> NumericParts, bool IsBeta) VersionKey(string v)
    {
        if (v == null) throw new ArgumentNullException(nameof(v));

        bool isBeta = v.Contains("-beta", StringComparison.OrdinalIgnoreCase);
        // Remove any "-beta" suffix (case-insensitive) before splitting
        string cleaned = v.Replace("-beta", "", StringComparison.OrdinalIgnoreCase);
        // Split on '.', parse each segment to int
        var numericParts = cleaned
            .Split('.', StringSplitOptions.RemoveEmptyEntries)
            .Select(p =>
            {
                if (!int.TryParse(p, out var n))
                    throw new FormatException($"Invalid numeric version component: '{p}' in version '{v}'");
                return n;
            })
            .ToList();

        return (numericParts, isBeta);
    }

    public static string GetNeoForgeVersion(string mcVersion)
    {
        if (string.IsNullOrWhiteSpace(mcVersion))
            throw new ArgumentException("Version must not be empty", nameof(mcVersion));

        var parts = mcVersion.Split('.', StringSplitOptions.RemoveEmptyEntries);
        if (parts.Length < 2)
            throw new ArgumentException(
                "Invalid Minecraft version format. Use format like '1.21.1'", nameof(mcVersion));

        // Skip the first component and re-join the rest
        return string.Join('.', parts.Skip(1));
    }

    private static int CompareVersionKeys(string a, string b)
    {
        var (pa, betaA) = VersionKey(a);
        var (pb, betaB) = VersionKey(b);

        int len = Math.Max(pa.Count, pb.Count);
        for (int i = 0; i < len; i++)
        {
            int na = i < pa.Count ? pa[i] : 0;
            int nb = i < pb.Count ? pb[i] : 0;
            int cmp = na.CompareTo(nb);
            if (cmp != 0)
                return cmp;
        }

        // if numeric parts equal, non-beta (false) comes before beta (true)
        return betaA.CompareTo(betaB);
    }

