    public async Task<string> DownloadServerpackInstaller()
    {
        WriteLog($"Downloading modpack installer for {ModpackName}");
        
        var os = Globals.ThisServer.OperatingSystem;
        var isWindows = os == EOperatingSystem.Windows;
        var installerFilename = Path.Combine(
            Globals.ThisService.RootDirectory, 
            isWindows ? "ftb-serverpack-installer.exe" : "ftb-serverpack-installer"
        );
        
        var osArg = isWindows ? "windows" : "linux";
        //{"valid_os_values":["windows","linux","darwin","freebsd","unix"],"valid_arch_values":["arm64","amd64","arm","x64"],"possible_values":["windows","arm64/windows","amd64/windows","linux","arm64/linux","amd64/linux","darwin","arm64/darwin","amd64/darwin","freebsd","arm64/freebsd","amd64/freebsd"]}
        var downloadUrl = $"https://api.feed-the-beast.com/v1/modpacks/public/modpack/{ModpackId}/{ModpackVersion}/server/{osArg}";

        // Download with progress reporting
        using var httpClient = new HttpClient();
        using var response = httpClient.GetAsync(downloadUrl, HttpCompletionOption.ResponseHeadersRead).Result;
        response.EnsureSuccessStatusCode();

        var contentLength = response.Content.Headers.ContentLength ?? -1L;
        using var downloadStream = response.Content.ReadAsStreamAsync().Result;
        using var fileStream = new FileStream(installerFilename, FileMode.Create, FileAccess.Write);
        
        var buffer = new byte[8192];
        var totalRead = 0L;
        var progress = 0;
        var lastProgress = -1;
        
        int bytesRead;
        while ((bytesRead = await downloadStream.ReadAsync(buffer, 0, buffer.Length, Globals.ThisCancellationToken)) > 0)
        {
            if(Globals.ThisCancellationToken.IsCancellationRequested)
            {
                httpClient.CancelPendingRequests();
                Globals.ThisCancellationToken.ThrowIfCancellationRequested();
            }
            await fileStream.WriteAsync(buffer, 0, bytesRead, Globals.ThisCancellationToken);
            totalRead += bytesRead;
            
            if (contentLength > 0)
            {
                progress = (int)((double)totalRead / contentLength * 100);
                if (progress != lastProgress && progress % 5 == 0)
                {
                    SetDisplayMessage($"Downloading modloader \"{Path.GetFileName(installerFilename)}\" ({progress}%)", progress);
                    lastProgress = progress;
                }
            }
        }

        UpdateProgress(progress);
        WriteLog("Modpack installer downloaded");
        return installerFilename;
    }

    public async Task InstallModpack(string installerFilename)
    {
        WriteLog($"Installing modpack {ModpackName}", 0);
        
        var os = Globals.ThisServer.OperatingSystem;
        var isWindows = os == EOperatingSystem.Windows;

        if (Globals.ThisServer.OperatingSystem == EOperatingSystem.Linux)
        {
            var chmod = Process.Start("chmod", $"+x {installerFilename}");
            chmod.WaitForExit();
        }

        var args = $"--pack {ModpackId} --version {ModpackVersion} --auto --force --no-java"; //https://github.com/FTBTeam/FTB-Modpack-Issues/issues/6312#issuecomment-2516899020

        var startInfo = new ServiceToolStartOptions
            {
                Executable = Path.Combine(Globals.ThisService.RootDirectory, installerFilename),
                WorkingDirectory = Globals.ThisService.RootDirectory,
                Arguments = args
            };

        //Get java version required by modpack
        var manifestUrl = $"https://api.feed-the-beast.com/v1/modpacks/public/modpack/{ModpackId}/{ModpackVersion}";
        using var httpClient = new HttpClient();
        var manifest = await httpClient.GetStringAsync(manifestUrl);
        var root = JsonSerializer.Deserialize<ManifestRoot>(manifest);
        var javaVersion = root.targets.FirstOrDefault(t => t.name == "java")?.version.Split('.')[0];
        string javaPath = Path.Combine(Globals.ThisService.RootDirectory, "java", $"java{javaVersion}", "bin", (isWindows? "java.exe":"java"));
        if(!File.Exists(javaPath) & Environment.GetEnvironmentVariable($"JAVA{javaVersion}") != null) // Use version installed separatelly
        {
            javaPath = Environment.GetEnvironmentVariable($"JAVA{javaVersion}");
        }

        if(!File.Exists(javaPath)) throw new Exception($"Could not find folder for Java version {javaVersion}");

        startInfo.EnvironmentVariables.Add("PATH", Path.GetDirectoryName(javaPath));
        
        var startResult = await Globals.ServiceManagerService.StartService(Globals.ThisService.ServiceId, startInfo);

        if(!startResult.Success) throw new Exception("Could not start installer process");

        var p = Process.GetProcessById(startResult.ProcessId);
        p.EnableRaisingEvents=true;
        UpdateProgress(-1);
        Globals.ThisCancellationToken.Register(() => {
            if(p != null && !p.HasExited) p.Kill(true);
        });
        await p.WaitForExitAsync(Globals.ThisCancellationToken);
        var exitCode = p.ExitCode;
        p = null;
        UpdateProgress(100);

        if (File.Exists(installerFilename)) File.Delete(installerFilename);
        
        WriteLog($"Installer exit code: {exitCode}");
        if(exitCode!=0)
        {
            var installerLog = Path.Combine(Globals.ThisService.RootDirectory, ".tca", "console.log");
            if(File.Exists(installerLog))
            {
                var newLog = Path.Combine(Globals.ThisService.RootDirectory, Path.GetFileNameWithoutExtension(installerFilename) + ".log");
                await Task.Delay(3000);
                File.Move(installerLog, newLog, true);
            }
            throw new Exception($"Installer failed with exit code {exitCode}. Check logs for details.");
        }
        
        WriteLog("Modpack installed");
    }

    public async Task SetCommandLine()
    {   
        WriteLog("Configuring commandline");

        // Delete previous command line
        var prevCmdLineKey=$"{_provider.InstallPrefix}:CustomModsCmd";
        if(Globals.ThisGameService.Variables.TryGetValue<long?>(prevCmdLineKey, out var prevCmdLineId))
        {
            WriteLog("Deleting previous commandline");
            await Globals.GameServiceManager.DeleteCustomCommandLine(Globals.ThisService.ServiceId, prevCmdLineId.Value);
        }

        // Find modloader jar in startup script
        var pattern = new Regex(@"((-jar .*jar|@libraries/net/.*.txt|@libraries\\net\\.*.txt))");
        var extension = Globals.ThisServer.OperatingSystem == EOperatingSystem.Windows ? "bat" : "sh";
        var startupScript = Path.Combine(Globals.ThisService.RootDirectory, $"start.{extension}");
        
        if (!File.Exists(startupScript))
            startupScript = Path.Combine(Globals.ThisService.RootDirectory, $"startserver.{extension}");
        
        if (!File.Exists(startupScript))
            startupScript = Path.Combine(Globals.ThisService.RootDirectory, $"run.{extension}");

        string modloaderJar = null;
        foreach (var line in File.ReadLines(startupScript))
        {
            var match = pattern.Match(line);
            if (match.Success)
            {
                modloaderJar = match.Groups[1].Value;
                break;
            }
        }

        var variables = new TCAdminVars() {
            {_provider.JarVariableName, modloaderJar}
        };

        var jarVar = $"${{{_provider.JarVariableName}}}";
        var cmdLineId = await Globals.GameServiceManager.UpdateCustomCommandLine(null, Globals.ThisService.ServiceId, 0, Globals.ThisTaskStep.DisplayName, variables);
        var cmdLine = await Globals.GameServiceManager.GetCustomCommandLine(Globals.ThisService.ServiceId, cmdLineId);
        cmdLine.Name=Globals.ThisTaskStep.DisplayName;
        cmdLine.OverrideDefault=true;
        
        if (!modloaderJar.Contains("@"))
        {
            cmdLine.CommandLine = cmdLine.CommandLine.Replace("-jar", string.Empty);
        }
        else
        {
            cmdLine.CommandLine = cmdLine.CommandLine.Replace($"-jar {jarVar}", modloaderJar);
        }

        await Globals.GameServiceManager.ApplyCustomCommandLine(null, Globals.ThisService.ServiceId, cmdLine.CmdLineId, false);
        // GameServiceManager ⬆️ updates ThisGameService.Variables so we have to reload it for ServiceManager
        Globals.ThisGameService.Variables = Globals.ServiceManager.TCAdminDbContext.GameServices
            .Where(x => x.ServiceId == Globals.ThisService.ServiceId)
            .Select(x => x.Variables)
            .FirstOrDefault();

        Globals.ThisGameService.Variables[prevCmdLineKey] = cmdLine.CmdLineId;
        Globals.ThisGameService.Variables[$"{_provider.InstallPrefix}:Type"] = "FTB";
        await Globals.ServiceManager.UpdateAsync(Globals.ThisService, true, default);
    }

    public string GetLevelName(string propertiesFile)
    {
        if (!File.Exists(propertiesFile)) return null;
        
        foreach (var line in File.ReadLines(propertiesFile))
        {
            if (line.StartsWith("level-name="))
            {
                var levelName = line.Split('=', 2)[1].Trim();
                WriteLog($"Level name detected as {levelName}");
                return levelName;
            }
        }
        return null;
    }

    public void DeleteWorldFolders(string levelName)
    {
        if (string.IsNullOrEmpty(levelName))
        {
            WriteLog("Level name is blank. Cannot delete world folders");
            return;
        }
        
        WriteLog("Deleting world folders");
        var basePath = Path.Combine(Globals.ThisService.RootDirectory, levelName);
        var folders = new List<string>
        {
            basePath,
            basePath + "_the_end",
            basePath + "_nether"
        };
        
        foreach (var folder in folders)
        {
            if (Directory.Exists(folder))
            {
                Directory.Delete(folder, true);
                WriteLog($"Deleted folder and its contents: {folder}");
            }
            else
            {
                WriteLog($"Folder does not exist: {folder}");
            }
        }
    }
