async Task DownloadAndExtractTgz(string url, string extractPath, CancellationToken cancellationToken)
{
    using(HttpClient downloadClient = new HttpClient())
    {
        var tgzFile = Path.Combine(extractPath, "tmp.tar.gz");
        var tFile = Path.Combine(extractPath, "tmp.tar");
        await downloadClient.DownloadFile(url, tgzFile, cancellationToken);

        using (FileStream compressedFileStream = File.OpenRead(tgzFile))
        using (FileStream outputFileStream = File.Create(tFile))
        using (GZipStream decompressionStream = new GZipStream(compressedFileStream, CompressionMode.Decompress, false))
        {
            decompressionStream.CopyTo(outputFileStream);
        }

        Directory.CreateDirectory(extractPath);
        TarFile.ExtractToDirectory(tFile, extractPath, true);

        File.Delete(tFile);
        File.Delete(tgzFile);
    }
}

async Task DownloadAndExtractZip(string url, string extractPath, CancellationToken cancellationToken)
{
    Directory.CreateDirectory(extractPath);
    using(HttpClient downloadClient = new HttpClient())
    {
        var zipFile = Path.Combine(extractPath, "tmp.zip");
        await downloadClient.DownloadFile(url, zipFile, cancellationToken);

        using(ZipArchive archive = ZipFile.OpenRead(zipFile))
        {
            foreach (ZipArchiveEntry entry in archive.Entries)
            {
                string destinationPath = Path.Combine(extractPath, entry.FullName);

                // Create directories if necessary
                Directory.CreateDirectory(Path.GetDirectoryName(destinationPath));

                // Extract file if it's not a directory
                if (!entry.FullName.EndsWith("/"))
                {
                entry.ExtractToFile(destinationPath, overwrite: true);
                }
            }
        }

        File.Delete(zipFile);
    }
}

public static void MoveFilesToParentFolder(string folderPath)
{
    if (string.IsNullOrWhiteSpace(folderPath) || !Directory.Exists(folderPath))
    {
        return;
    }

    string parentFolder = Directory.GetParent(folderPath)?.FullName;
    if (string.IsNullOrWhiteSpace(parentFolder))
    {
        return;
    }

    string[] files = Directory.GetFiles(folderPath);
    foreach (string file in files)
    {
        string destinationPath = Path.Combine(parentFolder, Path.GetFileName(file));

        // Ensure the file doesn't exist in the destination, otherwise rename it
        string uniqueDestinationPath = destinationPath;
        int counter = 1;
        while (File.Exists(uniqueDestinationPath))
        {
            string fileNameWithoutExt = Path.GetFileNameWithoutExtension(file);
            string extension = Path.GetExtension(file);
            uniqueDestinationPath = Path.Combine(parentFolder, $"{fileNameWithoutExt}({counter}){extension}");
            counter++;
        }

        File.Move(file, uniqueDestinationPath);
    }
}

