static List<int> FindWineProcess(CSharpGlobals Globals, string targetPath, string exe)
{
    var matchingPids = new List<int>();
    var procDirectory = new DirectoryInfo("/proc");

    foreach (var dir in procDirectory.GetDirectories())
    {
        // Only consider directories with numeric names (i.e. process IDs)
        if (int.TryParse(dir.Name, out int pid))
        {
            string cwdLinkPath = Path.Combine(dir.FullName, "cwd");
            try
            {
                // Create a FileInfo object for the "cwd" symlink.
                var cwdInfo = new DirectoryInfo(cwdLinkPath);

                // If the symlink exists, resolve its target.
                if (cwdInfo.Exists)
                {
                    // ResolveLinkTarget(true) follows the symlink.
                    var targetInfo = cwdInfo.ResolveLinkTarget(true);
                    if (targetInfo != null)
                    {
                        // Compare the full path of the symlink target with the desired targetPath.
                        if (string.Equals(targetInfo.FullName, targetPath, StringComparison.Ordinal))
                        {
                            var exeInfo = (new FileInfo(Path.Combine(dir.FullName, "exe")).ResolveLinkTarget(true));
                            var cmdline = File.ReadAllText(Path.Combine(dir.FullName, "cmdline"));
                            if(exeInfo.FullName == exe)
                            {
                                matchingPids.Add(pid);
                            }
                        }
                    }
                }
            }
            catch (Exception)
            {
                // Exceptions (e.g., due to permission issues) are ignored.
            }
        }
    }

    return matchingPids;
}

