using System;
using System.Diagnostics;
using System.IO;
using System.Net.Http;
using TCAdmin.SDK.Misc;
using TCAdmin.SDK.Misc.Extensions;

var Globals = new TCAdmin.Scripting.Engines.Addons.CSharpGameGlobals(); // DO NOT MODIFY THIS LINE

var gameFilesPath = Globals.ThisServer.Configuration.GameFilesPath;
var downloadedFile = Path.Combine(gameFilesPath, "WolfET.exe");
var patchFile = Path.Combine(gameFilesPath, "__ET_Patch_2_60.exe");

if (!Directory.Exists(gameFilesPath)) 
    Directory.CreateDirectory(gameFilesPath);

using (HttpClient downloadClient = new HttpClient())
{
    // Download and run WolfET.exe
    await downloadClient.DownloadFile("http://downloads.tcadmin.com/games/wolfet-windows.aspx", downloadedFile, Globals.ThisCancellationToken);

    var process = new Process();
    process.StartInfo.FileName = downloadedFile;
    process.StartInfo.Arguments = "/s";
    process.Start();
    await process.WaitForExitAsync(Globals.ThisCancellationToken);

    // Download and run ET_Patch_2_60.exe
    await downloadClient.DownloadFile("http://downloads.tcadmin.net/games/wolfet-update-windows.aspx", patchFile, Globals.ThisCancellationToken);
}

var patchProcess = new Process();
patchProcess.StartInfo.FileName = patchFile;
patchProcess.StartInfo.Arguments = "/s";
patchProcess.Start();
await patchProcess.WaitForExitAsync(Globals.ThisCancellationToken);

// Clean up downloaded files
File.Delete(downloadedFile);
File.Delete(patchFile);

// Determine install folder based on architecture
string installFolder;
if (Environment.Is64BitOperatingSystem)
{
    installFolder = Path.Combine(Environment.GetEnvironmentVariable("ProgramFiles(x86)"), "Wolfenstein - Enemy Territory");
}
else
{
    installFolder = Path.Combine(Environment.GetEnvironmentVariable("ProgramFiles"), "Wolfenstein - Enemy Territory");
}

// Copy game files to service folder
var filesFolderFullName = Path.Combine(gameFilesPath, Globals.ThisGame.FileAndDirectoryConfig.AutoSetupFolderName);
FileSystem.CopyDirectory(installFolder, filesFolderFullName);
